/*********************************************************************************************************************************
Logger Infrastructure: 

LOGGING LEVEL						DESCRIPTION															EXAMPLES
--------------------------------------------------------------------------------------------------------
SCUBE_LOG.warning 			Stream to log all the warnings  				Negative Cases 
SCUBE_LOG.error 				Stream to log all the errors  					Database Conn Error, NodeMailer Error
SCUBE_LOG.info 				Stream to log all the general events		UserID Add, Validated..etc
SCUBE_LOG.debug        Stream to log all developmentlogs				Dev logs only - NO PRODUCTIONS LOGS
*********************************************************************************************************************************/
var bunyan        = require('bunyan');
var stackTrace 		= require('stack-trace');
var colors        = require('colors');

/*Working*/
/*var warn_log	  = bunyan.createLogger({name: 'WARNING'});
var error_log 	  = bunyan.createLogger({name: 'ERROR'});
var info_log      = bunyan.createLogger({name: 'INFO'}); 
var debug_log     = bunyan.createLogger({name: 'APP_DEBUG'});*/

var bunyan_log    = bunyan.createLogger({name: ' '});

var logger = function () {

 	this.warning = function() {
 		for(var index =0; index < arguments.length ; index++){
 			bunyan_log.warn(arguments[index]);
 		}
 	}

 	this.error = function() {
 		for(var index =0; index < arguments.length ; index++){
 			bunyan_log.error(arguments[index]);
 		}
 	}

 	this.info = function() {
 		for(var index =0; index < arguments.length ; index++){
 			bunyan_log.info(arguments[index]);
 		}
 	}

 	this.debug = function() {
 		var log, file, frame, line, method;
	  frame = stackTrace.get()[1];
	  file = frame.getFileName();
	  line = frame.getLineNumber();
	  method = frame.getFunctionName();
	  log = " FileName:" + file + ":" + line + " \n Function: " + method + "() .\n Log:";

 		for(var index =0; index < arguments.length ; index++){
 			bunyan_log.info(log + arguments[index]);
 		}
 	}
}

module.exports = logger;
