var proximityCalculator = function() {

  // This function compares a pair of lat long  
  // co-ordinates and tells if the distance 
  // is within a specified range
  this.isProximityInRange = function(userLatLongObj, dbLatLongObj) {

    var distance = _getDistanceFromLatLonInMiles(userLatLongObj.lat,userLatLongObj.long,dbLatLongObj.lat,dbLatLongObj.long);
    // console.log(userLatLongObj.lat)
    // console.log(userLatLongObj.long)
    // console.log(dbLatLongObj.lat)
    // console.log(dbLatLongObj.long)
    console.log(distance);
    //console.log(userLatLongObj.radius_preference);
    if(distance <= userLatLongObj.radius_preference)
      return true;
    else
      return false;

  }

  function _getDistanceFromLatLonInMiles(lat1,lon1,lat2,lon2) {
    var R = 3959; // Radius of the earth in miles
    var dLat = _deg2rad(lat2-lat1);  // deg2rad below
    var dLon = _deg2rad(lon2-lon1);
    var a =
      Math.sin(dLat/2) * Math.sin(dLat/2) +
      Math.cos(_deg2rad(lat1)) * Math.cos(_deg2rad(lat2)) *
      Math.sin(dLon/2) * Math.sin(dLon/2);
    var c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    var d = R * c; // Distance in miles
    //console.log(d);
    return d;

  }

  function _deg2rad(deg) {
    return deg * (Math.PI/180)
  }
}
module.exports = proximityCalculator;